@import url('https://fonts.googleapis.com/css2?family=Berkshire+Swash&family=Merriweather+Sans:wght@300;400;700;800&display=swap');

* {
  box-sizing: border-box;
}

body {
  background-color: #fff;
  font-family: 'Merriweather', sans-serif;
  margin: 0;
  color: black;
  text-align: center;
  position: relative;
}

h1 {
  font-family: 'Berkshire Swash', sans-serif;
  font-size: 2.5em;
  margin-top: 0;
}

section {
  padding: 30px 0;
}

.section-heading {
  padding-top: 0;
  margin-top: 0;
  text-align: center;
  font-size: 1.7rem;
  font-weight: 800;
  color: black;
}

a,
input[type='submit'] {
  cursor: pointer;
}

input[type='submit']:focus {
  outline: 0;
}

input[type='submit']:active {
  transform: scale(0.95);
}

/* Header */
#header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px 15px 20px;
  max-height: 80px;
  height: 100%;
  background-color: #e7717d;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
}

.logo img {
  width: 150px;
  height: 70px;
}

#header ul {
  display: flex;
  align-items: center;
  justify-content: space-between;
  font-size: 1.2em;
  font-weight: 700;
}

.nav {
  padding-right: 30px;
}

.nav li {
  list-style: none;
  padding-right: 50px;
}

.nav-link {
  display: inline-block;
  width: 130px;
  text-decoration: none;
  color: black;
}

@media (max-width: 720px) {
  .nav li {
    padding-right: 25px;
    font-size: 0.8em;
  }

  .nav {
    padding: 0;
  }
}

@media (max-width: 650px) {
  .hamburger-menu {
    width: 35px;
    height: 30px;
    position: fixed;
    top: 25px;
    right: 20px;
    cursor: pointer;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    z-index: 2;
  }

  .line {
    width: 100%;
    height: 3px;
    background-color: #c3073f;
    transition: all 0.8s;
  }

  .change .line-1 {
    transform: rotateZ(-405deg) translate(-8px, 6px);
  }
  .change .line-2 {
    opacity: 0;
  }

  .change .line-3 {
    transform: rotateZ(405deg) translate(-8px, -6px);
  }

  .nav-bar {
    width: 300px;
    height: 100%;
    background-color: #262626;
    position: fixed;
    top: 0;
    right: -300px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    border-radius: 30% 0 0 60%;
    transition: right 0.8s cubic-bezier(1, 0, 0, 1);
  }

  .slide {
    right: 0;
  }

  #header ul {
    display: inline-block;
    justify-content: space-between;
    font-size: 1.2em;
    font-weight: 700;
    text-align: right;
  }

  .nav-link {
    text-decoration: none;
    color: #eee;
    font-size: 22px;
    font-weight: 300;
    letter-spacing: 1px;
    text-transform: uppercase;
    margin-top: 30px;
    position: relative;
    padding: 3px 0;
  }

  .nav-link::before,
  .nav-link::after {
    content: '';
    width: 100%;
    height: 2px;
    background-color: #c3073f;
    position: absolute;
    left: 0;
    transform: scaleX(0);
    transition: transform 0.5s;
  }

  .nav-link::after {
    bottom: 0;
    transform-origin: right;
  }
  .nav-link::before {
    top: 0;
    transform-origin: left;
  }

  .nav-link:hover::before,
  .nav-link:hover::after {
    transform: scaleX(1);
  }
}

/* Hero */
#hero {
  background-image: url(eating-cake.jpg);
  /* Photo by cottonbro from Pexels */
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  min-height: 580px;
  height: 100%;
  padding-top: 80px;
}

#hero h1 {
  color: #fff;
  font-weight: 700;
}

#hero h1,
.hero input[type='submit'],
.hero input[type='email'] {
  margin-top: 25px;
}

.hero input[type='submit'] {
  display: block;
  margin: auto;
  padding: 7px 20px;
  border: none;
  box-shadow: none;
  background-color: #e7717d;
  font-size: 1.2em;
  font-weight: 800;
}

.hero input[type='submit']:hover {
  background-color: #c3073f;
  transition: background-color 1s;
}

.hero input[type='email'] {
  max-width: 350px;
  width: 100%;
  padding: 5px;
  margin-bottom: 20px;
  font-size: 15px;
  border: none;
  text-align: start;
}

/* Features */
#features {
  min-height: 300px;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.grid h2 {
  font-size: 1.2rem;
  font-weight: 700;
}

.grid p {
  font-size: 1rem;
}

#features .grid {
  display: flex;
  justify-content: space-between;
  height: 150px;
}

#features .icon {
  flex: 1;
  padding: 25px 0 0 0;
  color: #c3073f;
}

#features .icon:hover {
  color: #e7717d;
  transition: color 1s;
}

#features .description {
  flex: 2;
  padding: 0;
  text-align: start;
}

/* How it works  */
.how-it-works {
  display: flex;
  justify-content: center;
}

#how-it-works {
  height: 500px;
}

.how-it-works iframe {
  max-width: 560px;
  width: 100%;
  height: 315px;
}

@media (max-width: 600px) {
  .how-it-works iframe {
    max-width: 350px;
    width: 100%;
    height: 200px;
  }

  #how-it-works {
    height: 350px;
  }
}

/* Popular Products */

#popular-cakes {
  background-image: url(licking-icing.jpg);
  /* Photo by cottonbro from Pexels */
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  background-attachment: fixed;
  color: #fff;
  position: relative;
  z-index: -1;
}

#popular-cakes .section-heading {
  color: #fff;
}

#popular-cakes::after {
  content: '';
  background-color: rgba(0, 0, 0, 0.3);
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: -2;
}

.cake {
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-evenly;
}

.cakes {
  border: 1.5px solid #c3073f;
  background-color: rgba(0, 0, 0, 0.4);
  max-width: 250px;
  width: 100%;
  height: 280px;
  margin-top: 30px;
  text-align: center;
}

.cakes h2 {
  font-size: 1.2em;
}

.cakes small {
  display: block;
}

.cakes .cake-type {
  background-color: rgba(0, 0, 0, 0.7);
  margin-top: 0;
  max-height: 40px;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
}

.cakes small {
  font-size: 1em;
  font-weight: 300;
}

.cakes h3 {
  margin: 0;
  padding-top: 0;
}

.cake input[type='submit'] {
  display: flex;
  margin: auto;
  padding: 5px 15px;
  border: none;
  box-shadow: none;
  background-color: #e7717d;
  font-size: 1em;
  font-weight: 700;
}

.cake input[type='submit']:hover {
  background-color: #c3073f;
  transition: background-color 1s;
}

@media (max-width: 800px) {
  .cakes {
    max-width: 180px;
    width: 100%;
  }
}

@media (max-width: 600px) {
  .cake {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
  }

  .cakes {
    max-width: 220px;
    width: 100%;
    margin-top: 20px;
  }
}

/* Footer */

footer {
  background-color: #e7717d;
  height: 80px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}

footer ul {
  margin: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 5px;
}

footer li {
  list-style: none;
  padding-right: 50px;
  font-size: 1em;
  font-weight: 700;
}

footer a {
  text-decoration: none;
  color: black;
}
